mapper.select:
select

from uctp_car_info a
inner join uctp_car_info_details b on a.id = b.CAR_ID
inner join uctp_contract_management c on b.CAR_ID = c.CAR_ID

    1=1


        and a.SALES_STATUS like #{pageVO.salesStatus}



        and a.CHECK_STATUS like #{pageVO.checkStatus}



        and a.BRAND like #{pageVO.brand}



        and (c.FIRST_PARTY like #{pageVO.searchValue}
        or c.SECOND_PARTY like #{pageVO.searchValue}
        or a.VIN like #{pageVO.searchValue}
        or a.BRAND like #{pageVO.searchValue})


        and a.PICK_UP_TIME between #{pageVO.PICK_UP_TIME[0]} and #{pageVO.PICK_UP_TIME[1]}


        and a.SALES_TIME between #{pageVO.SALES_TIME[0]} and #{pageVO.SALES_TIME[1]}


order by a.CREATE_TIME desc
